import { actionTypes } from './types';

const INTIAL_STATE = {
  hidden: true,
  cartItems: [],
};

const cartReducer = (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case actionTypes.TOGGLE_CART_HIDDEN:
      return {
        ...state,
        hidden: !state.hidden
      };

    case actionTypes.ADD_ITEM:
      return {
        ...state,
        cartItems :[ ...state.cartItems, action.payload ],
      } 

    default:
      return state;
  }
}

export default cartReducer;
